INSERT INTO `people` (`user_id`, `id_name`, `first_name`, `last_name`, `fathers_name`, `location`, `birth_date`, `death_date`, `separation_date`, `separation_end_date`, `separation_cause`, `separation_end_cause`, `age_at_separation_end`, `note`) VALUES
(1, 'richard_vahi', 'Richard', 'Vähi', NULL, 'misso', '10.5.1918', '29.3.1953', 'märts 1949', '29.3.1953', 'küüditatav', 'tapetud', '34', '1941 mobiliseeriti Punaarmeesse, oli sõjavangilaagris Saksamaal, vabastati, 1942. a tagasi kodus. 1949. a oli küüditatav. Asus varjama Misso lähedal. 7.3.1951 pääses Saika punkrilahingust koos Kuus Augustiga põgenema. Oli seejärel üks tagaotsitumaid mehi piirkonnas. 1951 varjus metsa ka tema abikaasa Elsa Vähi. Nende tütar kasvas kuni vanemate langemiseni punkris ja mitmetes Setumaa paljulapselistes peredes, hiljem Elsa Vähi õega. Richard Vähi langes koos abikaasaga Puutlipalu punkrilahingus 29.3.1953.'),
(2, 'august_kuus', 'August', 'Kuus', NULL, 'misso', '8.2.1923', '29.3.1953', 'märts 1949', '29.3.1953', 'küüditatav', 'tapetud', '30', 'Pärit Linnamäelt, Sõmerpalu vallast. Misso meierei juhataja, abielus apteekri tütre Evi Klaisiga, kes küüditati. 1951 liitus Richard Vähi salgaga. Saika punkrilahingus 7.3.1951 murdsid koos Richard Vähiga piiramisrõngast läbi ja pääsesid põgenema. Hukkus Puutlipalu punkrilahingus 29.3.1953'),
(3, 'karl_kaur', 'Karl', 'Kaur', NULL, 'misso', '10.2.1920', '29.3.1953', 'august 1944', '29.3.1953', 'põgenes pärast arreteerimist, oli teeninud Saksa sõjaväes', 'tapetud', '33', '1944 teenis Saksa sõjaväes, Võrus Jahtkomandos. Varjas end koos naise Melanie Kauriga Misso lähedal, hiljem Haanja ümbruses. Langes Puutlipalu punkrilahingus 1953. a. Tal sündis nädalapäevad pärast hukkumist poeg.'),
(4, 'melanie_kaur', 'Melanie', 'Kaur (s. Tuvi)', NULL, 'misso', '24.9.1923', '12.9.2006', 'august 1944', '7.10.1952', 'varjus koos abikaasaga', 'arreteeritud', '29', 'Varjas end koos mehe Karl Kauriga, algul Misso ümbruses, hiljem Haanja kandis. Arreteeriti Ruusmäe velskripunktis, kui oli esimest kuud lapseootel. Vanglas sünnitas poja. Mõisteti 25a, hiljem vähendati 8a-le, saadeti Mordva ANSV Dubravlagi, vabanes 20.5.1957. Hilisema nimega Melanie Laats. Autasustati 1999. a Kotkaristi IV klassi ordeniga.'),
(5, 'elsa_vahi', 'Elsa', 'Vähi (s. Eenla)', NULL, 'misso', '2.12.1924', '29.3.1953', '1950', '29.3.1953', 'küüditatav', 'tapetud', '28', 'Richard Vähi abikaasa. Oli koos mehega määratud väljasaatmisele. 1949-1950 varjas end Aleksander ja Julie Härma talus (JH oli Richard Vähi õde), 1950 läks metsa Alfred Eriku salka, 1951 ühinesid AE salgaga ka Saika lahingust pääsenud Richard Vähi ja August Kuus. Hukkus koos mehega Puutlipalu punkrilahingus 29.3.1953. Vähide tütre kasvatas üles tema õde Enda.'),
(6, 'august_kurra', 'August', 'Kurra', NULL, 'misso', '3.12.1912', '29.3.1953', 'suvi 1951', '29.3.1953', 'teenimine Saksa sõjaväes', 'tapetud', '40', 'Pärit Lätimaalt. Teenis Saksa sõjaväes, sattus Vene sõjavangi, hiljem vabanes. Vähi punkris alates suvest 1951, enne seda varjas Rudolf Koemetsa salga juures Lätimaal. Hukkus Puutlipalu punkrilahingus 29.3.1953.'),
(7, 'endel_leimann', 'Endel', 'Leimann', NULL, 'misso', '6.5.1914', '29.3.1953', '1949', '29.3.1953', 'põgenes küüditamise käigus', 'tapetud', '38', 'Iseloomustatakse kui äärmiselt tagasihoidlikku inimest. Pärit Kurepusa talust Perametsast, mis kuulutati kulaklikuks (68,20 ha, 2 hobust, 10 lehma). Ta ema, isa ja õde küüditati 25. märtsil 1949. Küüditamise operatiivrühma juht Petružko blokeeris selleks majapidamise, jättes valvesse Rahvakaitse (Hävituspataljoni)  laskuri Majas Edvarti (Augusti p) ja miilitsakursandi Uljanov Valentin Petrovitši. Perekonna kokkukogumise hetkel hüppas Leimann Endel teise kambrisse, sealt aknast välja ja jooksis metsa. Kursant Uljanov tulistas teda ja tormas järgi kuid kätte ei saanud. Leimann Endel asus end varjama vendade Pilvede salgas, 1951. a läks üle Vähi Richardi salka. Hukkus Puutlipalu punkrilahingus 29.3.1953. Tema kodutalu omastas pärast küüditamist Kündja kolhoos, elumaja ja osa kõrvalhooneid lammutati kohe, elumaja varemetele ehitas üks kolhoosnik endale hüti. Irkutskist väljasaatmiselt 1958. a naasnuna ostis Endeli isa Aleksander Leimann vana mehena oma lõhutud talu varemetele ehitatud hüti ja suri seal paar aastat hiljem. Võru Töörahvasaadikute Nõukogu Täitevkomitee on 15. jaan. 1970. a arutanud Leimann Aleksanderi talumajapidamise kustutamist kulaklike majapidamise nimekirjast, kuid otsustanud: "Teha ettepanek ENSV Ministrite Nõukogule jätta endiselt Leimann Aleksandri Jaani p talumajapidamine kulaklike majapidamiste nimekirja, sest omas küllaltki suure talu."'),
(8, 'leida_grunthal', 'Leida', 'Grünthal', NULL, 'misso', '24.5.1924', '29.3.1953', 'suvi 1949', '29.3.1953', 'küüditatav', 'tapetud', '28', 'Varjus küüditamise eest koos emaga. Isa oli olnud piirkonna Kaitseliidu ülem. Isa ja vend olid saanud Rootsi. Hukkus Puutlipalu punkrilahingus 29.3.1953.'),
(9, 'lehte_kai_ojamae', 'Lehte-Kai', 'Ojamäe (enne eestistamist Weisenberg)', NULL, 'misso', '23.5.1927', '29.3.1953', 'märts 1949', '29.3.1953', 'küüditatav', 'tapetud', '25', 'Kuulus Võru keskkoolis tegutsenud organisatsiooni Eesti Vabaduse Eest. Kuni 1951. a varjas koos ema ja isaga Haanja ümbruse taludes. 1951. a liitus Ala-Suhka lähedal laagris olnud Richard Vähi salgaga. Hukkus Puutlipalu punkrilahingus 29.3.1953.'),
(10, 'alfred_erik', 'Alfred', 'Erik', NULL, 'misso', '24.2.1910', '03.2001', 'september 1944', '28.2.1953', 'Kaitseliidus ja Omakaitses, varjus Vene mobilisatsiooni eest', 'arreteeritud', '43', 'Oli Kaitseliidus ja Omakaitses rühmaülemaks. 1.09.44 pidi asuma Nõukogude armee teenistusse, asus varjama. 1950 asus varjama ka tema abikaasa Erna Erik. Arreteeriti 28.02.1953 Härämäe punkrilahingu järel, kus hukkusid Arnold Lang ja tema abikaasa Erna Erik. Mõisteti 25+5, vabanes 28.2.68. Autasustati 1998. a Kotkaristi IV kl ordeniga. Suri 91 aastaselt, maetud Vana-Pärnu kalmistule.'),
(11, 'arnold_lang', 'Arnold', 'Lang', NULL, 'misso', '18.8.1922', '28.2.1953', 'suvi 1950', '28.2.1953', 'põgenes vanglast', 'tapetud', '30', 'Pildil Arnold Lang 4. klassi poisina aastal 1934.  Oli kuulunud Omakaitsesse. Abielus Herta Langiga, tütar ja poeg. Arnold Lang töötas Karisöödis metsaülemana ja tal olid kontaktid metsameestega (Mõttus jt). Hundi-Juku (Juhan Mäetalu) andis ta üles ja ta viidi Võru vanglasse. Seal nõuti temalt metsavendade uut asukohta. Kuu aega kestsid erimeetoditega ülekuulamised. Põgenes 1950. a kevadel Võru vanglast. Vanglast põgenema aitas ta üks tuttav vangivalvur. Asus varjama koos Alfred Erikuga. Hukkus Härämäe punkrilahingus 28.2.1953.'),
(12, 'roland_lang', 'Roland', 'Lang', NULL, 'misso', '9.4.1926', '30.9.1952', '1951', '30.9.1952', 'arreteeriti, nõuti venna väljaandmist', 'suri haavatasaamise tagajärjel', '26', 'Arnold Langi vend, Alfred Eriku salgast. Asus koos vennaga varjama metsas pärast seda, kui julgeolek arreteeris ta ja lasi pärast piinamisi ja värbamist lahti venna asukohta otsima. Sai raskelt haavata Veclaicene poeröövi ajal 16.9.52. Suri saadud haavadesse punkris 30.9.1952. Maetud Trooveri metsa punkri lähedale.'),
(13, 'vilbert_erik', 'Vilbert', 'Erik', NULL, 'misso', '29.9.1915', '18.6.1946', 'september 1944', '18.6.1946', 'oli Saksa sõjaväes velskriks', 'tapetud', '31', 'Alfred Eriku vend. Vilbert läks metsavennaks, abikaasa Adeele pojaga põgenes oktoobris 1944 Rootsi.Tapeti haarangu käigus 18.6.1946, kui ta oli külastamas Richard Vähi talu (samas tapeti ka Kaljo Laiv ja Nikolai Vähi). Alfred Erik varastas öösel venna surnukeha talust, kuhu see oli jäetud, ja mattis Tikka metsa punkri lähedale.'),
(14, 'erna_erik', 'Erna', 'Erik', NULL, 'misso', '7.6.1901', '28.2.1953', '1950', '28.2.1953', 'küüditatav', 'tapetud', '51', 'Varjas koos Alfred Erikuga. Langes 28.2.1953 Härämäe punkrilahingus. Poeg Voldemar Visk oli langenud 7.3.1951 Saika punkrilahingus.'),
(15, 'hartvig_sikk', 'Hartvig', 'Sikk', NULL, 'misso', '9.11.1921', '3.3.1968', '1945', '28.2.1953', 'Omakaitse, teenimine Saksa sõjaväes', 'arreteeritud', '32', '1941 Punaarmeesse mobiliseerituna olevat põgenenud rongilt, liitunud metsavendadega ja osalenud Tartu vabastamisel. Major Kure virgatsina saanud õlast haavata. Hiljem olnud Omakaitses. 1943. a lõpetas Võru gümnaasiumi ja astus Tartu ülikooli arstiteaduskonda. Oli enne töötanud ühe aasta oma venna Gustavi talus, kes mõrvati punaste poolt Tartu vanglas 1941. a, venelaste taganemisel enne sakslaste tulekut. Ülikoolist mobiliseeriti Harvig Saksa sõjaväkke. 1945 töötas ta velskrina Missos. Teda taheti arreteerida, asus 1945. a end varjama. Varjas end koos Alfred ja Vilbert Erikuga (Tikka metsas), ka koos Arthur ja Milbert Pilvega (Kivestiku metsas). Kuna põdes maohaavandtõbe, oli punkrielu talle vastunäidustatud, mistõttu sageli varjas end õe pool või ema juures Kasaritsas.  Arreteeriti 3.3.1953. Määrati 25a, vähendati 18a-le. Mordvas laagris oli röntgenoloog. Harrastas maalimist. Suri vabanemise päeval 1968 väljaspool Potma Likino laagri väravaid. Maetud Võru surnuaiale.'),
(16, 'rudolf_tomba', 'Rudolf', 'Tomba', NULL, 'misso', '4.2.1898', '7.3.1951', '1944', '7.3.1951', 'Vabadussõjas, Omakaitses, metsavend 1941', 'tapetud', '53', 'Pärit Pihkva kubermangust Rootova vallast Vruda külast. Võttis osa Vabadussõjast. Eesti ajal oli Petseris politseikonstaabel. Omakaitses. Metsavend 1941. Petseri RJM otsis teda taga, ta ühines Vähi salgaga 1949 lõpus-1950 alguses (rühma kuulusid sel ajal Vähi Richard, Vähi Arved ja Kask Karl ning legaalid Vähi Rafael ja Neumann Villu). Selle salgaga ühinesid veel Tullus Lembit, Pild Raimond, Keir Evald, Visk Voldemar, August Kuus ning 1950 a lõpus Tomba Rudolfi tütar Tomba Hedvig. 1950. a keskel eraldusid Vähi Arved ja Kask Karl. Jaanuaris 1951 ühines Vastseliinast vangistusest põgenenud Uibo Roland. Rudolf Tomba oli salga juhiks. Hukkus Saika punkrilahingus 7.3.1951.'),
(17, 'roland_uibo', 'Roland', 'Uibo', NULL, 'misso', '3.6.1921', '7.3.1951', '21. jaanuar 1951', '7.3.1951', 'põgenes arestist', 'tapetud', '29', '1944 mobiliseeriti Saksa sõjaväkke, sõja lõppedes naases koju. Abielus Elviira Uiboga, tütar. Töötas metsaülemana Sakutis. 1951. a arreteeriti kui 1941. a Suvesõja ajal Misso vallamaja ülevõtmises osalenu. Vangistuses peeti Vastseliinas, kus kuulati üle ja peksti. Põgenes koos Erik Gustavi, Soodla Viktori ja Verrev Eduardiga arestist ja asus varjama metsas Richard Vähi salgas. Hukkus Saika punkrilahingus 7.3.1951.'),
(18, 'raimond_pild', 'Raimond', 'Pild', NULL, 'misso', '10.4.1929', '7.3.1951', '1950', '7.3.1951', 'ei läinud Vene sõjaväkke', 'tapetud', '21', 'Hukkus Saika punkrilahingus 7.3.1951'),
(19, 'voldemar_visk', 'Voldemar', 'Visk', NULL, 'misso', '24.6.1921', '7.3.1951', 'september 1944', '7.3.1951', 'teeninud Saksa sõjaväes', 'tapetud', '29', 'Oli Alfred Eriku naise Erna poeg esimesest abielust. Olnud Saksa sõjaväes. Hukkus Saika punkrilahingus 7.3.1951.'),
(20, 'lembit_tullus', 'Lembit', 'Tullus', NULL, 'misso', '21.6.1931', '7.3.1951', '1948', '7.3.1951', 'põgenes, kui tuldi arreteerima', 'tapetud', '20', 'Elas Vastseliina mõisas, Mauri k/n. Isa arreteeriti 1947, vend 1944, ema oli küüditatud. Metsas alates 1948. Oli arreteeritav, kuid suutis varjuda, Vähi salgas alates 1950. Hukkus Saika punkrilahingus 7.3.1951.'),
(21, 'evald_keir', 'Evald', 'Keir', NULL, 'misso', '5.1.1919', '7.3.1951', '1948', '7.3.1951', 'nõuti metsavendade ülesandmist', 'tapetud', '32', 'Mobiliseeriti Vene armeesse, Porhovi all langes sakslaste kätte vangi. Treblinkas vangivalvur. Pärast puhkust tagasi ei läinud, varjas metsas. Sakslased said kätte, saadeti Kuramaale kindlustustöödele, kus langes venelaste kätte vangi. Pärast sõda tuli koju. Metsa läks nõudmise tõttu metsavendi välja anda. Hukkus Saika punkrilahingus 7.3.1951. Surmadokumenti pole. '),
(22, 'rafael_vahi', 'Rafael', 'Vähi', NULL, 'misso', '20.9.1920', '27.3.1953', 'kevad 1951', '27.3.1953', ' varjus arreteerimise eest', 'tapetud', '32', 'Varjus Saika sündmuste järel. Temast jäi maha poeg. Hukkus Viglasoo punkrilahingus 27.3.1953.'),
(23, 'vaino_harm', 'Väino', 'Härm', NULL, 'misso', '4.5.1935', '27.3.1953', 'kevad 1951', '27.3.1953', 'hoidumine arreteerimisest', 'tapetud', '17', 'Varjus Saika punkrilahingu järel, kui arreteeriti Richard Vähi sugulasi. Hukkus Viglasoo punkrilahingus 27.3.1953.'),
(24, 'milbert_pilv', 'Milbert', 'Pilv', NULL, 'misso', '29.8.1913', '15.3.1953', '1944 sügis', '15.03.1953', 'Omakaitse, Saksa sõjavägi', 'tapetud', '39', 'Pärit Kõo külast Vastseliina vallas. 1941 Suvesõjas osales Vastseliina vallamaja vabastamisel. Metsavend 1944. a sügisest.  Varjas koos venna Arthur Pilve jt-ga Perametsa, Savioja ja Kuura jõe ümbruses. Hukkus lahingus 15.03.1953 koos naise Mahti-Maimu Saarniidu ja vendade Tikerpuudega  Pabra järve lähedal, põgenemisel Vossa metsast. Arthur Pilvel ja Taisto Praksil õnnestus sellest lahingust põgeneda.'),
(25, 'mahti_maimu_saarniit', 'Mahti-Maimu', 'Saarniit', NULL, 'misso', '16.5.1927', '15.3.1953', '1949', '15.3.1953', 'küüditatav', 'tapetud', '25', 'Pärit Sandi külast Vastseliina vallast. Milbert Pilve abikaasa. Nende 1947. a sündinud tütar küüditati koos Mahti Saarniidu emaga 1949. a märtsis. Langes põgenemisel Pabra järve ääres koos mehe Milbert Pilve ja vendade Tikerpuudega 15.3.1953.'),
(26, 'artur_pilv', 'Arthur', 'Pilv', NULL, 'misso', '2.9.1902', '1.7.1953', '1944 sügis', '1.7.1953', 'Omakaitse, Saksa sõjavägi', 'tapetud', '50', 'Eesti Vabariigi sõjaväes allohvitser, kuulunud Omakaitsesse, teeninud Saksa sõjaväes. Fotograafia vallas pildistanud sõjaväe tegelasi ja olustikku, pildid Võrumaa muuseumi fotokogus. Abikaasa küüditatud. Langes haarangul Haavistu metsas Kuura silla juures 1.7.1953. Samas langes haavatult vangi Taisto Praks.'),
(27, 'taisto_praks', 'Taisto', 'Praks', NULL, 'misso', '1931', '7.12.1995', '1950', '1.7.1953', 'ingerisoomlasena Venemaale tagasi saadetav', 'arreteeritud', '22', 'Ingerisoomlasest orb Gatšina lähistelt. Tegi koos vanaisaga läbi ingerlaste evakueerimise Soome 1943 ja sealt repatrieerimise Venemaale, kodukohta ei lastud. 1945. a tuli 13-aastasena illegaalselt Venemaalt Eestisse, kus tee pealt korjas ta üles Pilt Julie mees Saviojalt, kelle tallu ta jäi tööle ja elama. Talus hakati teda kutsuma Soome-Volliks. Et soome rahvusest inimesed saadeti sundkorras Venemaale tagasi, asus end varjama. Algul Arvo Pilti punkris, edasi koos Arthur ja Milbert Pilvega. Arreteeriti haavatuna Haavistu metsas 1.7.1953. Samas lahingus langes Arthur Pilv. Määrati karistus 25+5, mille kandis Vorkuta, Mordva ja Permi laagrites. Vabanes 1978. a. Surnud 7.12.1995 Eestis. Maetud Koeru kalmistule.'),
(28, 'endel_tikerpuu', 'Endel', 'Tikerpuu', NULL, 'misso', '3.4.1919', '15.3.1953', 'sügis 1944', '15.3.1953', 'teeninud Saksa sõjaväes', 'tapetud', '33', 'Sündinud Pankjavitsa vallas Uusvada külas, kuni 1937 kandis eesnime Stefan. Oli Omakaitse liige, olnud Saksa sõjaväes. Metsavend al 1944. a sügisest. 1946. a sügisel oli 7-liikmelises salgas telgiga metsas Kooraste vallas. Talveks nad punkrit ei ehitanud, liikusid taludes Pühajärve, Otepää, Meremäe, Kasaritsa ja Sangaste vallas. Alates 1951 varjas koos oma venna Juhan Tikerpuu ja vendade Pilvedega ning Mahti Saarniiduga Vastseliina ja Ruusmäe vallas. Hukkus lahingus Pabra järve ääres, põgenemisel Vossa metsast punkrist, 15.3.1953. Surmadokumenti pole.'),
(29, 'juhan_tikerpuu', 'Juhan', 'Tikerpuu', NULL, 'misso', '20.4.1921', '15.3.1953', 'sügis 1944', '15.3.1953', 'Omakaitse', 'tapetud', '31', 'Sündinud Pankjavitsa vallas Uusvada külas, kuni 1939. a kandis eesnime Nikolai. Toimikutes esineb nimi nii Juhan kui Jaan Tikerpuuna. Varjus Koorastes. Alates 1951 varjas koos venna Endel Tikerpuuga vendade Pilvede salgas. Langes lahingus Pabra järve ääres, põgenemisel Vossa metsa punkrist 15.3.1953. Surmadokumenti pole.'),
(30,'viktor_telliste','Viktor','Telliste',NULL,'misso','1932',NULL,'15.11.1952','15.03.1953','soovimatus teenida Vene sõjaväes','arreteeritud','20a','Pärit Luhamaalt Hindsa külast. Ei ilmunud 1952. a kahele käsule vaatamata ajateenistusse, sügisel liitus Milbert Pilve salgaga. 15.03.1953. a punkrile toimunud haarangule järgnenud pikal põgenemisteekonnal astus Pabra järve juures ägedasse tulevahetusse jälitajatega. Kaotanud seal 4 punkrikaaslast surnutena ja aidanud 2 kaaslasel põgeneda neid automaaditulega kattes, suutis küll roomata metsani ja lahingukohast eemalduda, kuid u 10 km järel jäi ülikurnatuna jälitajate kätte. Kinnivõtjate sõnul oli ta pintsak ja isegi särk kuuliaukudest rebestatud. Mõisteti 25a vangistust ja 5a asumist, hiljem vähendati 8a.'),
(31, 'arnold_kallaste', 'Arnold', 'Kallaste', NULL, 'misso', '21.8.1905', '17.2.1953', 'september 1944', '7.10.1952', 'Omakaitses, varjus mobilisatsiooni eest', 'arreteeritud', '47', 'Elas Lauras, Saksa ajal abivallavanem. Esimesest abikaasast (kes suri) poeg ja tütar. Oli omakaitses. Võimu üleminekul 1941 võttis osa ühest punaaktivistide mahalaskmisest Laura lähedal. Metsa läks 1944. a. Varjas üksi punkris Tsirguvarikus, töötas seal rätsepana, naine Olga Kallaste oli sidepidajaks. 1952. a suvel tappis samas metsas Lind Augusti, kes teda nähes oli varjunud ja kes oli varem ähvardanud tema kinnivõtmise ja ta ema Siberisse saatmisega. Kinni võetud mürgi "Neptun" abil Tsirguvariku metsas agentide "Liiv" ja "Kask"tegutsemisel 7.10.1952. Maha lastud Moskvas Butõrkas 17.2.1953.'),
(32, 'rudolf_koemets', 'Rudolf', 'Koemets', NULL, 'misso', '25.9.1909', '1951', '1944', '1951 sügis', NULL, 'tapetud', '42a', 'Varjunimi "Insener". Lätimaa eestlane Aluksnest. Kirjeldus: "Väga ettevaatlik, punkris viibib harva, liigub maakonna piires palju ringi, käib aeg-ajalt Eestis. Hoiab enda lähedal üht portfelli dokumentidega, kaht püstolit ja granaati. Viina peaaegu ei võta, üliharvadel juhtudel. Haritud, alati korralikult pöetud ja raseeritud, maneeridelt aristokraat, sõltumata hetkest ja olukorrast." Rudolf Koemets pidas sidet Richard Vähi salgaga, samuti Oskar Kiudsooga. Eestlastega pidasid sidet ka tema salka kuuluvad Leonhard Rääbis, Velta Kromule ja keegi nimetuse all Saksapuizis. 1948 hankisid lätlased eestlastelt laskemoona. Adolf Eglitis tema salgast tapeti Vastseliinas 1948 julgeolekuagendi poolt varitsusse meelitatult. Koemetsa salk varjas end põhiliselt Virgulica jõe ümbruses Beja vallas Lätis, ohu korral Parmu metsas Eestis. Kuulduse kohaselt langes Rudolf Koemets punkrilahingus hilissügisel 1951, kattes tulega põgenema pääsenud kaaslasi. Langemiskoht teadmata, võib olla ka Eesti pinnal. (vt. lingid: Elviira Jukk. Minu pere lugu keerukatest aastatest 1940-1951; ja Arvo Pilti mälestused). '),
(33, 'leonhard_raabis', 'Leonhard (Leo)', 'Rääbis (Rääbus)', NULL, 'misso','10.01.1924' , NULL, 'september 1944', NULL, 'vältis mobilisatsiooni punaväkke', 'legaliseerus', NULL, 'Lätimaa eestlane, Markalne vallast Ragusala talust. Rääbise emapoolseid sugulasi elas Vastseliina vallas Möldri külas. Lätis teati nime all Leon Rabuzis (vene kirjapildis ka Rabuž). Varjunimed "Atvars" ja "Puika" (ehk "Poiss"). Aktiivne metsavend, 1945. a salka kuulusid Voldemar Keiris, Adolf Eglitis, Nikolai Pakalns, Artur Priede, Herbert Gogulis. Julgeoleku poolt tagaotsitav alates 1946. Varjas koos Velta Kromulega end põhiliselt Virgulica ümbruses Lätis Beja vallas, punker oli Krustosalo metsas, ohu korral Parmu metsas Eestis (vt. lingid: Elviira Jukk. Minu pere lugu keerukatest aastatest 1940-1951). L. Rääbis pääses eluga Virgulica punkrilahingust 8.11.1951, kui pärast punkri juures toimunud plahvatuses meelemärkuse kaotamist teadvusele tulles suutis eemalduda. Velta Kromule raskelt haavatuna jäi ründajate kätte. Selles lahingus langes lisaks kaks meest ja üks väga noor tütarlaps pikkade valgete juustega, kohalikele tundmatud, tüdruk olevat rääkinud eesti keelt. Leo Rääbis pidas juba 40-ndatel sidet Vähi salgaga, kellega veel 1952. a augustis korraldati koos toidu hankimist kauplustest. Hiljem lahkus Kurzeme kanti, legaliseerus seal, abiellus ja elas naise perekonnanimega.'),
(34, 'velta_kromule', 'Velta', 'Kromule', NULL, 'misso', '17.4.1922', '8.11.1951', '1947', '8.11.1951', 'kirjutas nõukogudevastaseid lendlehti', 'tapetud', '29a', 'Eestis teati teda varjunimedega Aina (vt. lingid: Elviira Jukk. Minu pere lugu keerukatest aastatest 1940-1951) ja Inga. Lätlanna, pärit Beja vallast Aluksne maakonnast. Oli ühes grupis koos Leonhard Rääbise ja Rudolf Koemetsaga. Varem kahel korral lendlehtede pärast võimude poolt kinni peetud. Tapeti (suri) raskelt haavatuna Virgulica punkrilahingu järel 8.11.1951. Samas lahingus langesid lisaks 2 meest ja üks väga noor tütarlaps, keda kohalikud ei tundnud ja oletasid eestlasteks, kuna tüdruk oli rääkinud eesti keelt.'),
(35, 'oskar_kiudsoo', 'Oskar', 'Kiudsoo', NULL, 'misso', '4.2.1901', '10.6.1961', 'sügis 1944', '3.3.1953', 'Misso vallavanem', 'arreteeritud', '59', 'Oli Misso vallavanem nii Eesti Vabariigi ajal (al. 1934) kui Saksa okupatsiooni ajal. Abielus, 4 poega. Võttis osa Misso vallamaja ülevõtmisest 1941. a Suvesõjas, kus osales Lätimaalt sakslaste käest relvade hankimises. Võeti kinni punkrist koos Arvo Piltiga 3. 3.1953. Karistuseks määrati 25a, vähendati 15a-le. Vabanes 4.07.1960.'),
(36, 'elmar_kiudsoo', 'Elmar', 'Kiudsoo', NULL, 'misso', '15.9.1909', '24.6.1974', NULL, '01.01.1952', 'Saksa sõjaväes ja Omakaitses. Osales 1941. a Misso vallamaja juures toimunud lahingus.', 'arreteeritud', '42', 'Määrati 25 pluss 5'),
(37, 'linda_pilt', 'Linda', 'Pilt (s. Tirul)', NULL, 'misso', '1923', '29.8.2017', 'aprill 1949', '28.02.1953', 'küüditatav', 'arreteeritud', '30', 'Kalju Telli naiseõde. Mees teenis Saksa armees ja suri sõjavangilaagris. Varjas end koos Kalju Telliga Erik Gustavi punkris, hiljem Erik Alfredi punkris. Arreteeriti Härämäe punkrilahingu järel, mõisteti 25+5.'),
(38, 'kalju_tell', 'Kalju', 'Tell', NULL, 'misso', '12.9.1921', '29.5.1992', 'aprill 1949', '28.2.1953', 'raha omastamine Krediidipangas', 'arreteeritud', '31', 'Oli Punaarmees leitnandi auastmes. Asus metsas varjama pärast segaseid seadusvastaseid rahatehinguid Misso Krediidipangas. 1951. a varjas end koos Pilt Lindaga Erik Gustavi punkris Parmu metsas. Alfred Eriku punkrisse varjama asus 1952. a sügisel. Arreteeriti 28.2.1953 pärast Härämäe punkrilahingut.'),
(39, 'arvo_pilt', 'Arvo', 'Pilt', NULL, 'misso', '9.2.1895', '10.9.1989', '1949', '3.3.1953', 'Kaitseliit, Omakaitse, oli Vastseliina vallavanem', 'arreteeritud', '58', 'Varasem Vastseliina vallavanem. Abielus, 4 poega ja tütar. Võeti kinni koos Oskar Kiudsooga punkris Läti piiril. Määrati 25+5, vähendati 12a 6k-ni.'),
(40, 'aleksander_harm', 'Aleksander', 'Härm', NULL, 'misso', '1896', '?', 'kevad 1951', '27.3.1953', 'hoidumine arreteerimisest', 'arreteeritud', '57', 'Olnud tsaariarmees, Kaitseliidus ja Omakaitses. Abielus Julie Vähiga, poeg ja tütar. Tema peret ähvardas Saika punkrilahinguga seoses arreteerimine. Arreteeriti Viglasoo punkrilahingu järel, kus langes tema poeg, 17 aastane Väino Härm ja Rafael Vähi, kes püüdsid piiramisrõngast läbi murda. Määrati 25 a, vähendati 10 a.'),
(41, 'julie_harm', 'Julie', 'Härm', NULL, 'misso', '1899', '25.11.1977', 'kevad 1951', '27.3.1953', 'hoidumine arreteerimisest', 'arreteeritud', '54', 'Richard Vähi õde. Abielus, poeg ja tütar. Läks metsa koos perega arreteerimise eest pärast Saika punkrilahingut, kus vend Richard pääses põgenema. Arreteeriti Viglasoo punkrilahingu järel 27.3.1953. Määrati 10+5. Vabanes 1955.'),
(42, 'richard_siegert', 'Richard', 'Siegert', NULL, 'misso', '25.3.1925', '16.7.1951', NULL, '16.7.1951', NULL, 'tapetud', '26', 'Tapeti haarangul varahommikul heinateol 16.7.1951. Samal haarangul pääses Edgar Liira põgenema.'),
(43, 'edgar_liira', 'Edgar', 'Liira', NULL, 'misso', '13.3.1922', '15.9.2008', '1944', '28.7.1953', 'põgenemine pärast arreteerimist, Saksa sõjaväes teenimine', 'legaliseerus', '30', 'Alates 1949. a suvest hoidusid teised metsavennad temast eraldi, enne seda varjas koos Kauridega (1944-1945), Alfred Eriku ja Voldemar Viskiga (kevad 1947-kevad 1949). Süvenes joomine ja tüli norimine. 1950. a tappis punaaktivisti Lõokese, kelle surma  kandis julgeolek 1951. a Saika punkri meeste arvele, ehkki 1950. a tunnistati toimepanijana Liirat. Legaliseerus juuli lõpus/augusti alguses 1953. Arreteeriti Lõokese Elmari tapmise pärast 1964. a.'),
(44, 'gustav_erik', 'Gustav', 'Erik', NULL, 'misso', '24.3.1913', '1944 sügis', '21.1.1951', '9.7.1951', 'põgenes Vastseliinast arestist', 'tapetud', '38', 'Oli osa võtnud Misso vallamaja vallutamisest 1941. a. Omakaitses oli jaoülemaks. Abielus Salme Erikuga, 4 last. Venelaste tulles varjas end kahel perioodil ajavahemikus 1944-1947. Kolmandat korda asus end varjama pärast Vastseliinas arestist põgenemist koos Uibo Rolandi, Verrev Eduardi ja Soodla Viktoriga 21.1.1951. Hiljem varjas end Parmu metsas koos Tell Kalju, Papp Jaani ja Pilt Lindaga. Tegutses ka koos Lätimaa eestlaste Rääbis Leo ja Koemets Rudolfiga, samuti koos Vähi Richardi salgaga. Hukkus Lätimaal  pärast 9.7.1951 Ponkulis purjuspäi sooritatud poeröövi. Proovides rukkipõllus jõuda metsani, avas ta jälitajatega tulevahetuse,  mille käigus sai  ränki haavu alakõhtu ja suri järgmisel päeval Aluksne haiglas.'),
(45, 'auleid_toomsalu', 'Auleid', 'Toomsalu', 'Julius', 'rouge', '28.3.1922', '5.2.1952', 'mai 1948', '9.2.1951', 'laulis seltskonnas keelatud laulu (Jää vabaks Eesti meri).', 'arreteeritud', '28', 'Pärit Rõuge vallast Tealase külast. Vanemad elasid Rõuges, isa oli tal invaliid, ema al 1950. a  vangistatud. Auleid oli mobiliseeritud nii Saksa kui Vene armeesse. Metsavennaks läks 1948. a kevadel koos onupoja Kalju Kirbitsaga, kui teda ähvardas arreteerimine rahvalike laulude laulmise eest ühel sünnipäeval. Metsas oli tal hüüdnimeks „Karl“ ja „Kapten“. Tegutses koos paljude Viitina kandi meestega, samuti  Simo Pihlapuu salgaga. 9. veebruaril 1951 Auleid Toomsalu arreteeriti ja sõjatribunal mõistis talle 12.10.1951 surmaotsuse. Tapeti 5.2.1952.'),
(46, 'herbert_virk', 'Herbert', 'Virk', 'Eduard', 'rouge', '13.4.1924', '23.7.1949', '1947', '23.7.1949', 'põgenes kinnipidamiskohast', 'tapetud', '25', 'Sündis Vastseliinas. Pärast mõlema vanema surma kolmekümnendatel aastatel võeti ta Viitinasse Richard Pihlapuu peresse kasupojaks. Deserteerus nõukogude armeest, metsa läks 1947. a pärast põgenemist kinnipidamiskohast. Hüüdnimi oli metsas „Kusta“. Lisaks Toomsalule tegutses ka koos Endel Otsaga (röövisid 1948 päeva ajal Rõuges panka jpm) ja Simo Pihlapuuga. Tapeti koos kahe kaaslasega julgeolekuüksuse korraldatud haarangul Viitinas Haki külas Pulli talus 23. 7.1949.'),
(47, 'vambola_kuklane', 'Vambola', 'Kuklane', 'Aksel', 'rouge', '20.3.1931', '23.7.1949', '1949', '23.7.1949', 'varjus küüditamise eest', 'tapetud', '18', 'Pärit Viitina vallast Ortumäelt, kus ta isal ja onul oli suur Aabra talu (101 ha). Mõlemad pered tehti kulakuks ja määrati küüditamisele. Märtsis 1949 varjuti, Vambola läks metsa Auleid Toomsalu salka. Hüüdnimi oli tal „Jumbo“. Tapeti 18a tulevahetuses julgeolekuüksuse korraldatud haarangul Viitinas Haki külas Pulli talus 23.7.1949.'),
(48, 'elmar_pettai', 'Elmar', 'Pettai', 'Edgar', 'rouge', '19.6.1928', '23.7.1949', 'kevad 1949', '23.7.1949', 'liitus sõpradega', 'tapetud', '21', 'Õppis Väimela Põllumajanduskoolis. Isa oli renditalunik Viitina vallas Haki külas Pulli talus, kus tutvus ümbruskonna metsavendadega. Metsavendadest sõpradega ühines kevadel 1949. Metsas oli hüüdnimi  „Jakob“.  Tapeti haarangul Pulli talus 23.7.1949.'),
(49, 'eduard_kirbits', 'Eduard', 'Kirbits', 'Jaan', 'rouge', '1.1.1921', '23.2.1951', '1944', '23.2.1951', 'teenimine Eesti Leegionis', 'tapetud', '30', 'Pärit Luutsnikult. Oli teeninud Eesti Leegionis. Metsa läks 1944. a., hüüdnimi oli „Jüri“või „Ats“. Olevat 945. a osalenud rünnakus metsavendi jahtinud miilitsavolinikule Adolf Noortootsile. 1949-1950 varjas koos Elmar Pärliga. Tapeti koos venna Aksel Kirbitsaga tulevahetuses punkrist põgenemisel 23.2.1951.'),
(50, 'aksel_kirbits', 'Aksel', 'Kirbits', 'Jaan', 'rouge', '10.10.1924', '23.2.1951', 'märts 1949', '23.2.1951', 'varjumine küüditamise eest', 'tapetud', '26', 'Pärit Luutsnikult. Varjus koos vanematega küüditamise eest. Ühines Auleid Toomsalu salgaga, hüüdnimi oli \"Ori\", „Orin“ (või „Orik“). Alates 1949. a elas metsapunkris koos Leida Pettaiga. 1950. a novembris sündis neil tütar Milvi. Tapeti koos venna Eduard Kirbitsaga tulevahetuses punkrist põgenemisel 23.2.1951.'),
(51, 'valter_pallo', 'Valter', 'Pallo', 'Adam', 'rouge', '14.8.1907', '5.2.1952', '1947', '15.5.1951', 'oli teeninud Saksa sõjaväes nmeditsiiniohvitserina', 'arreteeritud', '43', 'Pärit Varstu Tagakülast. Koolis käis Rõuges, hiljem töötas möldrina. Sõja ajal oli Tartus Saksa sõjaväehospitalis velsker, pärast Berliini Sõjaväemeditsiini Akadeemia lõpetamist arst-assistent. Enne metsa minekut elas Tartumaal. Varjama hakkas 1947, alates 1949 kuulus Auleid Toomsalu salka. Hüüdnimi oli „Doktor“, kuna ravis inimesi. Sugulased väidab olevat surnud: isa Adam 1944, ema Liis 1945, naine Ottilie 1943. Vend Ferdinand, saksaaegne Varstu vallavanem, oli arreteeritud 1944. a. Julgeolekule eitab laste olemasolu, tegelikult 3 tütart (Lukk Ottiliega, Lepp Valvega ja Jürisoo Adeelega). Sõjatribunal määras talle surmanuhtluse. Tapeti vanglas.'),
(52, 'kalju_kirbits', 'Kalju', 'Kirbits', 'Oskar', 'rouge', '19.8.1929', '6.2.1949', '1948', '6.2.1949', 'Võttis osa rünnakust miilitsavolinik Noortootsile.\r\nArreteeriti 1945. a ja vabastati nõudega teised metsavennad reeta. Koos Toomsaluga hakkas varjama alates 1948 maist.', 'tapetud', '19', 'Sündis Viitina vallas. Kuulus Võru keskkooliõpilaste nõukogudevastasesse\r\npõrandaalusesse ühendusse. Varjama hakkas 1945. a, pärast rünnakut\r\nmiilitsavolinik Noortootsile järgnenud arreteerimist septembris 1945 ja\r\nsellele järgnenud vabastamist koos nõudmisega reeta teisi. Ta liitus\r\nRohelise Partisanide Pataljoni Lükkä (Lükka, Nursi) punkri metsavendadega. Lükkä punkri ründamise ajal 28.12.1945 ei viibinud ta punkris, vaid oli läinud jõuluks vanematekoju. Edasi varjas kodus ja sugulaste juures Läänemaal. Uuesti\r\nläks metsa koos Auleid Toomsaluga 1948. a, hüüdnimi oli \"Jim\".\r\nTapeti hommikupoole ööd haarangu käigus, sünnipäevapeo ajal Ortumäel\r\nKuklaste juures 6.2.1949.'),
(53, 'paul_liit', 'Paul', 'Liit', 'Aleksei', 'rouge', '11.1.1926', '5.2.1952', 'teistkordselt 1949', '21.3.1950', 'soovis võidelda vägivaldse võimu vastu, oli tapnud kohaliku küüditamisorganisaatori', 'arreteeritud', '24', 'Vanemad pärit Petserimaalt Suure-Buravitsa külast, elas Haanjas Koke külas. Astus vabatahtlikuna 33. Politseipataljoni, septembris 1944 mobiliseeriti Punaarmeesse, kust novembrikuus deserteerus. Oli metsas 1944-1946, legaliseerus. Uuesti läks metsa 1949, ühines Auleid Toomsalu salgaga, hüüdnimi „Haanja Miis“ või „Juhan“. Arreteeriti 1950. a, temast jäi maha tütar. Sõjatribunal määras surmanuhtluse, tapeti 05.2.1952.'),
(54, 'henn_kirbits', 'Henn', 'Kirbits', 'Oskar', 'rouge', '25.6.1933', '27.5.1978', '1949', 'november 1949', 'küüditamine', 'legaliseerus', '16', 'Pärit Viitina vallast. Metsas oli varjus küüditamise eest. Tema vend Kalju Kirbits (19a) tapeti haarangul 1949. a veebruaris. Henn Kirbits pidevalt metsavennana ei elanud, ehkki julgeolekutoimikutes on teda nimetatud Toomsalu salga liikmeks. Hüüdnimi metsas oli „Putilane“. Saksa ajal ta alaealisena mobilisatsiooni alla ei kuulunud. 1949. a lõpus vastu talve legaliseerus. Arreteeriti 1. märtsil 1951. Sõjatribunal määras vangistust ja asumist 25+5. Vabanes 1956.'),
(55, 'endel_ots', 'Endel', 'Ots', 'Friedrich', 'rouge', '12.12.1927', '1978', '1947', '1949', 'deserteerus nõukogude armeest', 'arreteerimine', '21', 'Meremäe elanik, tema isa elas Kasaritsas, ema oli surnud, vend arreteeritud ja saadetud 1945. a Komi ANSV-s vangilaagrisse. 1942. a astus Endel Ots vabatahtlikult 20. SS-diviisi.Taandus lahingutega Emajõe äärest Riiga, kus 20. SS diviisi riismed nimetati ümber 12. jalaväediviisiks ja suunati lahingutesse Tandaga (peaks vist olema Dundaga) linna – Kurmendiasse (peaks vist olema Kurlandiasse ehk Kuramaale), kus sõdis nõukogude vägede vastu, kuni märtsis 1945 viidi sõdurid üle mere Danzigisse. Võitles kuni Saksamaa kapituleerumiseni. Nooremohvitseri ja jaokomandöri abina teenis lahingutes Raudristi 1. klassi. 9. mail jäi nõukogude vägede kätte sõjavangi ja saadeti Komi ANSVsse, kust vabanes 1946 lõpus ja saadeti Narva tööpataljoni, NSVL „BO“ (vene tähed) ehitusele. 1947. a deserteerus sealt ja liikus Rõugesse, kus kohtus Kalju Kirbitsa ja Herbert Virkiga. Muudetud sünniaastga sünnitunnistuse alusel, mille tegi Saareall Asta Rõuge TK-st, sai endale passi. Tegutses koos Kalju Kirbitsa ja Herbert Virkiga, kuni arreteeriti 1949. a ja määrati Toomsalu ja Virki salga liikmena vangistusse ja asumisele 25+5a.'),
(56, 'simo_pihlapuu', 'Simo (Simu, Samuel)', 'Pihlapuu', 'Daniel', 'rouge', '18.2.1916', '23.9.1949', '1944', '1949', 'teenimine Saksa sõjaväes', 'tapetud', '33', 'Pärit Rõuge vallast Saarlase külast. Teenis Saksa sõjaväes. Metsa läks 1944. a,  hüüdnimi oli  „Vana Jüri“. Vend Aksel suri Siberis vangistuses, vend Arthur oli saadetud 10 a Siberisse, vend Aaro oli koos Simoga metsas. Oli piirkonna metsavendade juhiks. Tapeti jälitamisel Sadra külas 23.9.1949.'),
(57, 'heino_uibo', 'Heino', 'Uibo', 'August', 'rouge', '29.2.1924', '5.10.1955', 'oli metsavend ka Saksa ajal, uuesti 1944. a', '5.10.1955', 'võõra armeeteenistuse vältimine', 'tapetud', '31', 'Heino Uibo (ristinimega Heino Axel Uipuu) oli pärit Mõniste vallast. Oli metsavend ka Saksa ajal, taas metsas 1944. a. Hüüdnimi metsas oli „Joonas“. Neljakümnendate lõpus varjas end koos elukaaslase Linda Tatrikuga, kellega sündis 1949.a kevadel poeg. Magava lapse tapsid julgeolekusõdurid punkris haarangul 30. juunil 1950. Heino Uibo tapeti julgeoleku haarangul tulevahetuses 5.10.1955 Lätis, Druskase külas. Linda Tatrik langes seal vangi.'),
(58, 'aksel_sprenk', 'Aksel', 'Sprenk', 'Jaan', 'rouge', '24.1.1908', '30.6.1950', '1944. a august', '30.6.1950', 'Oli Kaitseliidu Rõuge piirkonna malevkonna juht ', 'tapetud', '42', 'Oli Eesti ajal Kaitseliidu Rõuge malevkonna pealik. Metsas alates 1944. a, hüüdnimi „Vunts“. Tapetud Roobi metsas 30.6.1950. Temast jäi maha abikaasa poja ja 2 tütrega.\r\nAksel Sprenki ülestähendused (päevik) on toodud käesoleval lehel alajaotuses MUU/LINGID.'),
(59, 'linda_tatrik', 'Linda', 'Tatrik', 'August', 'rouge', '4.3.1924', '?', '1949', '5.10.1955', 'küüditatav', 'arreteeritud', '31', 'Pärit Viitina vallast. Varjus 1949, kuna kuulus küüditamisele. Teistel andmetel läks metsa 1948. a. Isa August Tatrik ja ema Emmi Tatrik olid 1945. a vangistatud, vend Eduard Tatrik (s 1922) varjas end metsas. Tema kodus elas ta tädi Saia Emilie. Koos oma elukaaslase Heino Uiboga varjas Linda Tatrik end seal kevadel 1949 poja sündimise ajal ja hiljem haigestumise korral. Nende aasta ja kahekuuse poja tappis julgeolek 30. 07.1950 Roobi metsas punkris. Linda Tatrik  arreteeriti 1955. a Druskase külas Lätis.'),
(60, 'elmar_parli', 'Elmar', '(Perli) Pärli', 'Jakob', 'rouge', '02.2.1919', '25.9.1950', '1944', '25.9.1950', 'teenimine Saksa sõjaväes', 'tapetud', '29', 'Pärit Luutsnikult. Teenis Saksa vägedes. Metsas alates 1944,  hüüdnimed „Tuks“, „Vana Pini“. Pääses ainsana põgenema punkrilahingust 3.04.1946 Luhasoos, kus hukkusid August Eelsoo, Rein Kask ja Olev Loog. Tapeti Pillardi külas Enn Siidra talus haarangul 25.9.1950.'),
(61, 'leida_pettai', 'Leida', 'Pettai', 'Edgar', 'rouge', '21.3.1933', '16.5.1997', '1949', '23.2.1951', 'põgenemine reetjaks sundimise eest', 'arreteeritud', '17', 'Pärit Viitina vallast Pulli talust. Pärast venna Elmari hukkumist metsavennana sundis julgeolek teda metsavendade äraandjaks, augustis 1949. a oli ta sunnitud varjuma metsa. Sai metsavend Aksel Kirbitsa elukaaslaseks, kellega sündis tütar. Haarangul 23.02.1951 tapeti tulevahetuses Aksel ja Eduard Kirbits, Leida Pettai arreteeriti. Laps võeti ära ja pandi lastekodusse. Leidale määrati 25+5a vangistust ja asumist.'),
(62, 'valve_lepp', 'Valve', 'Lepp', 'August', 'rouge', '5.8.1929', '?', 'sügis 1949', '1949. a detsember', 'läks metsavennale kaaslaseks', 'lahkus varjama inimasustussse Palli külla', '20', 'Pärit Viitina vallast. Varjama hakkas end sügisel 1949. a koos Valter Palloga Luhasoos. Lapseootuse tõttu lahkus sealt 1949. a lõpus või 1950. a algul. Arreteeriti 23.2.1951 ja määrati vangistust ja asumist 25+5, laps eraldati lastekodusse.'),
(63, 'eduard_mottus', 'Eduard', 'Mõttus', 'Juhan', 'rouge', '4.11.1906', '23.7.1953', '1944', '23.7.1953', 'oli olnud politseikonstaabel', 'tapetud', '46', 'Oli olnud konstaabel, metsas varjas end koos oma abikaasa Miralda Mõttuse, Valter Pallo jt. Tapeti julgeoleku poolt 23.7.1953.'),
(64, 'miralda_mottus', 'Miralda', 'Mõttus', 'Tõnis', 'rouge', '23.7.1918', '26.12.2000', '-', '13.11.1953', '-', 'arreteeritud', '35', 'Varjas end 1948. a koos Eduard Mõttuse, Valter Pallo, Harri Mõttuse, hiljem koos Voldemar Rüütliga. Arreteeriti 13.11.1953, määrati 2+5. Vabanes invaliidina 1955.'),
(65, 'voldermar_ruutli', 'Voldemar', 'Rüütli', 'Paul', 'rouge', '12.8.1908', '28.4.1950', '1944', '28.4.1950', 'oli olnud Kaitseliidus', 'tapetud', '41', 'Elas Varstu vallas Matsi külas, oli olnud kaitseliitlane. Metsas oli alates 1944. Alates 1948 varjas end koos Eduard Mõttusega. Tapeti 28.4.1950.'),
(66, 'harri_mottus', 'Harri', 'Mõttus', 'Jaan', 'rouge', '1920', '?', '1944', '26.1.1954', 'oli olnud Kaitseliidus', 'arreteeritud', '34', 'Pärit Varstust. Arreteeriti 26.01.54, vabanes 26.6.54.'),
(67, 'juhan_mottus', 'Juhan', 'Mõttus', 'Jaan', 'rouge', '1903', '?', '1949', '23.2.1951', 'poja Sulev Mõttuse arreteerimine', 'arreteeritud raskelt haavatuna', '48', 'Pärit Ruusmäelt. Metsa läks 1949. a pärast poeg Sulev Mõttuse kinnivõtmist, kes arreteeriti Lembit Labi pealekaebuse alusel. Hüüdnimi oli metsas „Vana Hull“. Langes vangi punkri juures tulevahetuses raskelt haavatuna 23.2.1951.'),
(68, 'aksel_kuklane', 'Aksel', 'Kuklane', 'Jakob', 'rouge', '21.5.1897', '?', '1949', '-', 'varjus küüditamise eest', '-', '-', 'Pärit Rõuge vallast Abra talust. Metsa läks küüditamise eest. Toomsalu salgaga ühines tema poeg Vambola Kuklane.'),
(69, 'aaro_pihlapuu', 'Aaro (Arnold)', 'Pihlapuu', 'Daniel', 'rouge', '6.12.1907', '1.2.1979', '1949', '1951', 'küüditamine', 'arreteeritud', '44', 'Aaro Pihlapuu oli kooliõpetaja Ristemäel ja koolijuhataja Pikakannul. Metsas alates 1949. a. Arreteeriti 1951. a ja määrati 25a vangistust. Vabanes 1956. Aaro Pihlapuu pidas metsavennana ka päevikut, mis on avaldatud: „Pihlapuude võitlus“, Hotpress 2009'),
(70, 'valter_kuklane', 'Valter', 'Kuklane', 'Nikolai', 'rouge', '16.4.1926', '10.3.2008', '1949', '6.5.1949', 'küüditamine', 'arreteeritud', '22', 'Pärit Aabra talust Viitinast, varjus metsa küüditamise eest. Arreteeriti koos venna Hugoga 6.5.1949. Määrati 25 a vangistust, vabanes 1956.'),
(71,'adolf_eglitis','Adolf','Eglitis',NULL,'misso','?','13.10.1947',NULL,'13.10.1947',NULL,'tapetud',NULL,'Lätlasest metsavend lätieestlase Rudolf Koemetsa salgast. Kasutas ka nime Janis Sagamež. Osales 1947. a Lätis Aluksne maakonnas Beja metsas toimunud Markalne ja Malupe valdade metsavendade kokkutulekul, kus A. Eglitis ja R. Koemets rääkisid laskemoona saamisest Eesti metsavendadelt. Adolf Eglitis langes 1947. a Vastseliinas. Sinna meelitas ta Sakuti külas Tõdur Põllu juures ajutist talutöölist etendanud julgeolekuagent "Välk" ehk Karl Leonhard Paulov. 6.09.1947 oli agent “Välk” loonud Läti metsavendadega, kellest üks on eestlane ja 2 lätlased ja kes varjasid end Karajüri külas, Lāzberģise vallas ja Valga maakonnas, Läti NSVs. Metsavennad pöördusid “Välgu” poole, et viimane organiseeriks neile ülesõidu Rootsi ja andsid talle ülesande otsida Tallinnas selleks võimalusi. Julgeolek plaanitses “Välgu” abiga meelitada metsavennad rongi Eesti territooriumile ja nad Tartus kinni pidada. 13.10.1947 toimus “Välgul” Julgeoleku ülesandel uus kohtumine metsavendadega ja kuna viimased olid Rootsi mineku osas ümber mõelnud, juhtis agent "Välk" omal algatusel ühe metsavenna Eesti NSV territooriumile Vastseliina, kus agent jootis kõrtsis metsavenda veiniga ja teatadas sellest Julgeoleku operatiivpunkti. Kinnivõtmisel tulistas metsavend surmavalt MGB Võru operatiivpunkti ülemat n. ltn. Stepankovi. Tulevahetuse käigus lasid hävituspataljoni võitlejad illegaali maha. Tapetult saadi püstol ja dokumendid KIRULIS JANES Jakobi p nimele, tegeliku nimega EGLITIS, pärit Aluksne rajoonist, Läti NSV-st. Kirulise kotist leiti automaat, kompass ja üks kirjutusmasinal löödud lätikeelne nõukogudevastane lendleht. Agendi juhatusel võeti kinni ka PÕLD, TÕDUR Samoili p, kellelt võeti ära erinevad topograafilised kaardid ja 10 nõukogudevastast lendlehte, analoogsed tapetud Kirulise juurest leituga.'),
(72,'august_koemets','August','Koemets','Jaan','rouge','1.10.1887','30.7.1950',NULL,'30.7.1950',NULL,'tapetud','62a','Mäe-Saki talu peremees, varjas end koos Aaro Pihlapuuga Roobi metsas. Langes operatsioonis, milles RJM ründas korraga kolme eraldi paiknevat punkrit: tema (August Koemetsa), Aksel Sprenki ja Heino Uibo punkrit. Teistes punkrites langesid Aksel Sprenk ja aastavanune Heino Uibo ning Linda Tatriku poeg.  Teistel õnnestus pääseda. August Koemetsa poeg Aleksei Koemets oli langenud punkrilahingus 5.11.1945'),
(73,'aleksei_koemets','Aleksei','Koemets','August','rouge','16.07.1916','5.11.1945',NULL,'5.11.1945','teeninud Saksa sõjaväes','tapetud','29a','Varjas end punkris koos Simo Pihlapuu ja Johannes Ülenurmega. Langes punkri ründamisel RJM vägede poolt, Simo Pihlapuul ja Johannes Ülenurmel õnnestus põgeneda.'),
(74,'ants_aunver','Ants','Aunver',NULL,'sangaste','13. 01.1910','3.01.1959','august 1944','10.1953','küüditamisnimekirjas juunis 1941','lahkumine punkrist Võrumaal, Põhja-Eestisse','43a',' Hdn "Villi". Keema-Mihkli talu peremees Linnamäe vallast Võrumaalt (talus 48 ha põldu, 18 karilooma, 3 hobust). Varjus juuniküüditamise ajal 1941, Saksa ajal oli Omakaitses ja töötas Võrus vangivalvurina, pidades oma talu. Alates taasokupeerimisest 1944 elas metsavennana järjest 14 aastat, enamuse sellest ajast (8 a)  maa-alustes punkrites. Viimased 5 aastat elas ja töötas vale nime all Kehra lähedal Pirga saekaatris. Arreteerimise (18.08.1958) ja surma mõistmise järel tegi vanglas ennetava enesetapu 3.01.1959.'),
(75,'aino_reinumägi','Aino','Reinumägi',NULL,'sangaste','13.01.1921','6.04.2006','kevad 1950','11.1953','küüditamise eest varjumine ja legaliseerumisel äraandjaks sundimine','lahkumine punkrist Võrumaal Põhja-Eestisse','32a','Oli metsavendade abistaja, tema kodus  Pätsimäel Urvaste vallas oli Johannes Heeskal jt varjamiskoht. Märtsiküüditamise järel hakkas end varjama. Püüdes 1949 sügisel legaliseeruda, survestas Julgeolek teda äraandjaks. Aino Reinumägi põgenes surve eest Ants Aunveri juurde punkrisse. Elas koos Aunveriga maa-alustes punkrites 1950-1953. Alates 1953. a sügisest elas varjajana vale nime all Kose rajoonis Soodlas. Arreteeriti 18.08.1958, määrati 8 a vangistust, vabanes Mordva ANSV Dubravlagist 18.08.1966. Pärast vabanemist elas Jõgeval.'),
(76,'johannes_heeska','Johannes','Heeska',NULL,'sangaste','17.06.1910','7.05.1991','detsember 1944','26.03.1953','metsavend juunis 1941, Omakaitse','arreteerimine','43a','Hdn Jõll, ka Paul, Jaanis, Kalju, Timo. Heeska talu peremees, varjas end nii 1. Vene okupatsiooni kui ka Saksa ajal. Sõjajärgse metsavennana käis läbi Urvaste, Sõmerpalu, Kooraste metsavendadega. Kirjeldus: Pikka kasvu, tugeva kehaehitusega, pealagi paljas, sirge suure ninaga, alati relvastatud. Kannab poolkasukat, säärsaapaid. Kinni võeti Loosu-Navi külas. Mõisteti 25+5, oli Karaganda obl. Kamõšlagis, Irkutski obl. Ozjorlagis, Mordva ANSV Dubravlagis. Vabanes 10.12.1971.'),
(77,'robert_heeska','Robert','Heeska',NULL,'sangaste','5.01.1913','16.07.1965','august 1945','24.11.1956','deserteerumine Nõukogude armeest','arreteerimine','43a','Hdn Kapten, Kapi, Hinno. Oli koduväiks Kulli talus Linnamäe vallas, mis kuulutati kulaklikuks. Varjas koos abikaasaga.  Tütar (s. 1939) küüditati koos naise vanematega.  Kinni võeti punkrist Lanna metsas. Mõisteti 15 a vangistust ja 5a asumist. Suri vangistuses Mordva ANSV Dubravlagis 16.07.1965.'),
(78,'kalju_visnapuu','Kalju','Visnapuu',NULL,'sangaste','23.03.1926','1.08.1948','veebruar 1945','1.08.1948','deserteerumine Nõukogude armeest','tapetud','22a','Hdn Jeekim, Ööbik, Pääsukene. Põgenes Kuramaale rindele suunduvast ešelonist. Varjama hakkas end Uhtjärve ümbruses Urvastes. Kirjeldus: Lühikest kasvu, tugeva kehaehitusega, ümara näoga, tume šatään, pikkade taha kammitud juustega, nina keskmine, sirge. Relvastatud püstoliga. Langes Inuri talus hävituspataljonlase kuulist.'),
(79,'artur_telling','Artur','Telling',NULL,'sangaste','18.08.1928','28.12.1948','aprill 1947','28.12.1948','põgenemine arestist','tapetud','20a','Hdn Jõlõp. Läks 15 aastasena vabatahtlikuna Saksa sõjaväkke. Sõja järel õppis Otepääl koolis, kus võeti direktori kabinetis rahvakaitselaste poolt kinni ja pandi Kooraste TK-sse aresti. Põgenes sealt akna kaudu ja läks metsavennaks. Tegutses Urvaste, Linnamäe ja Sõmerpalu vallas. Kirjeldus: Pikka kasvu, normaalse kehaehitusega, brünett, väikesed vuntsid, kannab prille ja ninal on sünnimärk. Riietest kandis tumerohelist sportpluusi ja kalifeesid, jalas kummikud. Brauningu rihm kabuuriga mantli all. Relvadest omas kaht püstolit ja automaati PPŠ. Langes varitsuses (Aliida) Alandi talu juures Urvaste asunduses (Urvaste mõisas). Langemisel kandis musta värvi kaitseliidu vormiriietust. Julgeolek mattis salaja “ühe tellisetehase” territooriumile.'),
(80,'feliks_hõbe','Feliks','Hõbe',NULL,'sangaste','13.04.1919','17.08.1953','1945','17.08.1953','deserteerus Nõukogude armeest tööpataljonist','tapetud','34a','Hdn Jokker, Kusta, Fell. Kuulus Antsla Omakaitsesse. Teenis Saksa armees 1941-1944. 1944  a. mobiliseeriti nõukogude armeesse tööpataljoni, kust ta 1945. a deserteerus ja hakkas end Uhtjärve ümbruses varjama. Kirjeldus: Keskmist kasvu, tugeva kehaehitusega (teisal et kõhn), kitsa näoga, tumedapäine. Relvadeks 2 püstolit ja Saksa automaat. Kandis valget poolkasukat, jalas säärsaapad.   Tapeti julgeoleku mõrvaragendiks “Jüri” värvatud endise juristi ja Saksa aegse Võru linnapea Edgar Henningu poolt une pealt kirvega.'),  
(81,'august_kängsepp','August','Kängsepp',NULL,'sangaste','28.12.1918','29.06.2009','1944','9.01.1949','varjamine mobilisatsiooni eest', 'arreteeritud', '30a', 'Saksa ajal elas oma kodutalus (Savisaare talu) Pritsi külas Linnamäel. Omakaitsesse ei kuulunud. 1944 veebruaris mobiliseeriti Saksa armeesse, kust saadeti Kloogale tööpataljoni . Põgenes teel ja pöördus koju tagasi. Varjas Saksa võimude eest punkris oma talus. Vene väkke mobilisatsiooni vältides varjas end edasi kodutalus ka Vene võimude eest ja tegutses ühistes ettevõtmistes koos Johannes Heeska, Karl Kuusi, jt.-ga. 1947 a. märtsis käis Tartus, kus proovis Koidula tn elava Helmi Orava tehtud võltsitud (2000 rbl maksnud) tõendiga saada linna TK-st endale dokumente. Sealt nõuti MVD külastust, kuid Orav oli hoiatnud MVD-sse (siseministeeriumi) minemast. Vajalikud dokumendid oma nimele sai ta Orava käest lisamaksu eest1948 a. Oma talus ta enam varjata ei saanud ja suundus mais 1947 möldriks Helmesse Kaubi veskisse, kus töötas oma nime all kuni arreteerimiseni 1949 a. Mõisteti 25 a. Vabanes 1960. a.'),
(82,'karl_kuus','Karl','Kuus',NULL,'sangaste','24.03.1915','25.05.1972','1944','17.06.1947 (teisal 14.07.1947)','töötamine politseis','arreteeritud','32a','Hdn Aspirant, Rantjee e Rant. Saksa ajal töötas politseikonstaablina Linnamäel ja Pärnus Tali vallas. Kuni veebruar 1944 teenis politseipataljonis nr. 206. Alates 1944 varjas punkris isakodus (Ala-Põdral) Linnamäe vallas. Tegutses 1946. a  koos vendade Heeskate, August Kängsepa, Jeeger Valteri, Org Akseli jt.-ga, varjates end kodutalus. Mais 1947 lahkus Misso, vend August Kuusi juurde (kes langes metsavennana 1953. a Puutlipalu punkris). Arreteeriti sama aasta (1947) juunis Missos. Määrati 25+5, Magadani obl. Sevvostlag. Vabanes laagrist 1957 a ja asumiselt 1963 a.'),
(83,'daniel_peterson','Daniel','Peterson',NULL,'sangaste','0.00.1894','0.00.1955','?','0.00.1951','teeninud Saksa sõjaväes','legaliseerunud?','57a','Hdn Under, Unter. Varjas koos Valter Jeegeriga punkris Tammetalu metsas. Võttis osa suurest rongiröövist ja veel mitmest ettevõtmisest. Kirjeldus: u 50 aastane, keskmist kasvu, kannab musta või sinist poolmantlit. Varjamiskoht oli tal ka Sõmerpalu vallas Kohk Peetri juures. Ilmus 1951. a koju Lepistu valda, kodustel polnud teadmist, kust ta enne seda oli. Suri 1955. a.'),
(84,'valter_jeeger','Valter','Jeeger',NULL,'sangaste','22.04.1921','6.09.1951','1944','6.09.1951','teeninud Saksa sõjaväes','tapetud','31a','Hakkas metsavennaks pärast lahkumist Saksa armeest, kuhu oli värvatud 1944. Alates 1949. a varjas koos venna Edgar Jeegeriga. Kirjeldus: Keskmisest pikemat kasvu, tugeva kehaehitusega, ovaalse näo ja sirge ninaga brünett. Kannab pikka halli palitut, halli mütsi, säärsaapaid. Vasakus käes peenike kuldsõrmus sinise kiviga. Relvadeks 2 püstolit ja Soome automaat. 1947 hankis endale fiktiivsed dokumendid nimele “Tuul”. Varjas kodutalu maadel Sõmerpalus, Sõmerpalu mõisa lähedal ja Osulas. Langes Osula lähedal varitsuses 6.09.1951. Andmed selleks andis julgeolekule agent “Andrei” ehk värvatud lähedane abiline Hans Taalfeld.'),
(85,'edgar_jeeger','Edgar','Jeeger',NULL,'sangaste','16.11.1922','24.10.1952','1949','24.10.1952','varjus omaste küüditamise järel','tapetud','29a','Töötas enne metsa minekut Harjumaal Saida sovhoosis ja Kõue vallas Kabaja sovhoosis agronoomina. Oli sunnitud isa arreteerimise järel siirduma koos vennaga metsavennaks. Varjas end punkris Sõmerpalu mõisa maadel, Osulas, Jaan Saare juures Vagulas Navi külas. Langes kinnivõtmisel varitsuses, mis oli seatud tema kohtumisele ühe naisterahvaga.'),
(86,'johannes_ülenurm','Johannes','Ülenurm',NULL,'sangaste','24.04.1908','22.10.1962','1944','23.04.1951','töötas politseinikuna','arreteeritud','43a','Ants Aunveri tsõdsepoeg.Varjas end juuni keskel 1941 koos Johannes Heeskaga Pulli ja Heeska küla lähedal. Alates 1944 varjas algul Kanepi vallas, siis Rõuge vallas ja Ristemäel, alates oktoobrist 1946 kuni 1947 Valgamaal Leo Sidorovi nime all, seejärel uuesti Rõuge vallas. Elas Rõuge vallas 5.11.1945. a üle punkrirünnaku, mille käigus murdis koos Simo Pihlapuuga välja, kolmas kaaslane Aleksei Koemets langes seal. 1948. a Ülenurm legaliseerus. Julgeolekule tegi pealekaebuse 31.05.1950 oma küla mees, punane Voldemar Rosse, kes oli teda Keenis näinud ja ära tundnud. Alates 31.10.1950 töötati teda läbi formulaartoimikus (isiku jälitamiseks sisse seatud operatiivtoimik) nr 11059, nimetusega Estnatsionalist. 23.04.1951 peeti ta kinni ja arreteeriti. Mõisteti 25+5. Vabanes 1955.'),
(87,'aksel_pallav','Aksel','Pallav',NULL,'sangaste','6.09.1923','1.04.1946','1944','1.04.1946','hoidumine mobilisatsioonist','tapetud','22a','Hdn Fakiir. Varjas end koos Ants Aunveri, Johannes Heeska ja teistega 1945 Võhandu jõekäärus koopas, Utita veskis jm. Tegutses koos Johannes Heeskaga. Langes Sõmerpalu vallas  Määritsa talu lahingus väljamurdmiskatsel.'),
(88,'avo_pruus','Avo','Pruus',NULL,'sangaste','5.09.1914','1.04.1946','1944','1.04.1946','oli seotud Eesti Vabariigi Rahvuskomitees tegutsejatega','tapetud','32a','Hdn Advokaat, Šveik. Töötas Saksa ajal Võru raudteejaamas kassiirina. Tegutses kahasse Eesti Vabariigi Rahvuskomiteega seotud August Püviga.  1944. a suhtles Aunveri ja Heeskaga viimase naiseisa Kivi Augusti talus, Pruus ise varjas end koos Lukki, Keemi ja Pähniga. Langes lahingus (põletati sisse) Sõmerpalu vallas Määritsa talus, kus langesid kuus metsavenda ja 3 abilist.  Mahapõlenud maja ahjulõõri peidetud pudelist leiti tema metsavendade läkitus eesti rahvale: "Eesti rahvas! Täna, 1. aprillil 1946. a. meie, Eesti partisanid, võitlesime tiblade ja eesti rahva äraandjate vastu. Avaldasime kuuekesi vastupanu vaatamata suurele ülekaalule umbes kaheksa tundi. Niisiis, eesti rahvas, võidelge visalt eesti rahva vabaduse ja iseseisvuse eest! Vapper sõdur Švejk Elagu vaba Eesti ja eesti rahvas! Tervitus kõigile kaaslastele, väikesele õekesele, isale ja emale ja veel kord väikesele H-le. Tervitab Avo Pruus".'),
(89,'peeter_rahnulaid','Peeter','Rahnulaid',NULL,'sangaste','9.03.1915','1.04.1946','?','1.04.1946','?','tapetud','31a','Hdn. Timmo (Timo). Pärit Tartumaalt, varjas end 1944 detsembris koos Heeska ja Aunveriga Võhandu jõekäärus kaldakoopas Pulli küla lähedal. Langes Määritsa talu lahingus, (põles sisse).'),
(90,'kalju_torop','Kalju','Torop',NULL,'sangaste','4.02.1918','12.12.1973','?','16.07.1947','teeninud Saksa sõjaväes','arreteeritud','29a','Pärit Sõmerpalu vallast. Teeninud Saksa sõjaväes. Pääses oma kodus Sõmerpalu vallas Punakülas Koidu talus toiminud lahingust 27.04.1946 põgenema. Seal hukkusid tema isa Samuel Torop, Arkadi Hanschmitt, Villi Kupp ja Kalju Täht. Osales koos Johannes Heeskaga 1947 Tsoorust Antslasse viidava või röövil. Oli esimesel vankril. Teist vankrit hakkas Julgeoleku operatiivgrupp jälitama, sellel olnud, August Märdimäe ja Leonard Šteinbach tapeti, Kalju Visnapuu pääses põgenema. Arreteerimise järel mõisteti Kalju Toropile 25 a, oli Komi ANSV Sevželdorlagis, Mordva ANSV Dubravlagis, vabanes 3.03.1961.'),
(91,'harri_haller','Harri','Haller e Haldma',NULL,'sangaste','22.05.1913','6.07.1946','1944','6.07.1946','töötas politsei konstaablina','tapetud','33a','Pärit Linnamäe vallast. Varjas end kodu lähedases punkris Ala-Põdral. Ühel koduskäigul varitsesid teda miilitsavolinik Kohlberg paari rahvakaitse mehega. Talle tulistati jalgadesse ja lasti oma koduõuel verest tühjaks joosta.'),
(92,'arnold_kängsepp','Arnold','Kängsepp',NULL,'sangaste','23.04.1910','2.09.1949','1944','2.09.1949','deserteerumine Punaarmeest vahetult enne Saaremaale rindele saatmist','tapetud','39a','Pärit Linnamäe vallast. Varjas end koos küüditamise eest varjava Johannes Krüneriga. Agendiks “Ott” värvatud Elsa Orasmäe leppis Julgeoleku käsul Arnold Kängsepaga kokku kohtumise Linnamäe TK lähedal, kus viimane püüdis varitsust märgates põgeneda, aga lasti maha.'),
(93,'august_märdimäe','August','Märdimäe',NULL,'sangaste','31.05.1911','25.09.1946','1944','25.09.1946','töötas Võrus politseis','tapetud','35a',' Varjas end vanematekodus Urvastes. Naine Linda elas Võrus. Kuni 1946 olevat varjanud koos Šteinbachiga ka Linnamäe valla “Lembitu” kolhoosi esimehe Viktor Reinvarti juures. Langes Tsoorust Antslasse viidava võikoorma üle võtmisel. Võikoorem oli jagatud kahele vankrile, millest Linnamäele liikuvat vankrit hakkas Julgeoleku operatiivgrupp jälitama. Ora veski lähedal toimunud tulevahetuses langesid August Märdimäe ja Leonhard Šteinbach, Kalju Visnapuu pääses põgenema. Julgeoleku ettekanne: 25.09.1946 nägid julgeolekusõdurid varitsuses Utsali talu juures Linnamäel kl 23 õhtul 4 liikmelist metsavennagruppi, keda asuti jälitama. Tekkinud tulevahetuses MVD sisevägede 138. laskurpolgu võitlejatega tapeti 2 metsavenda, teistel õnnestus varjuda. Varitsejatest tapeti punaarmeelane Korjakin ja haavati jefr. Rahmatulinit ja punaarmeelast Kolinintsevit. Tapetud metsavendi ei tuvastatud.'),
(94,'voldemar_pähn','Voldemar','Pähn',NULL,'sangaste','4.11.1923','?','12.02.1945','23..09.1945','deserteerumine Eesti 27. Laskurdiviisist','arreteeritud','22a','Enne sõja algust töötas Tallinnas naftabaaasis, Evakueerus 27. aug. 1941 Suure Tõlluga Leningradi, sealt üle Laadoga Novosibirskisse, kus elas kuni 6. veebr. 1942. Veebruaris 1942 mobiliseeriti 27 Eesti Laskurdiviisi, kus teenis kuni 12.02.1945. Kui väeosa saadeti Kuramaale rindele, jäi meelega oma väeosast maha ja deserteerus. Suundus Linnamäele Kõuhkna tallu August Kivi juurde, kus olid ka Johannes Heeska ja Ants Aunver. Võttis osa August Tiituse hukkamisele toimetamisest. Pärast arreteerimist määrati 10+5, oli Kirovi obl. Vjatlagis, Karaganda obl. Steplagis, Krasnojarsi krais. Vabanes 1955. a.'),
(95,'leonhard_šteinbach','Leonhard','Šteinbach e Steinbach',NULL,'sangaste','19.01.1915','25.09.1946','?','25.09.1946','?','tapetud','30a','Pärit Veskimõisast, Kambja kihelkonnast, Tartumaalt. Kuni 1946 olevat varjanud koos August Märdimäega Linnamäe valla “Lembitu” kolhoosi esimehe Viktor Reinvarti juures. Langes Tsoorust Antslasse viidava võikoorma üle võtmisel. Võikoorem oli jagatud kahele vankrile, millest Linnamäele liikuvat vankrit hakkas Julgeoleku operatiivgrupp jälitama. Ora veski lähedal toimunud tulevahetuses langesid August Märdimäe ja Leonhard Šteinbach, Kalju Visnapuu pääses põgenema. Julgeoleku ettekanne: 25.09.1946 nägid julgeolekusõdurid varitsuses Utsali talu juures Linnamäel kl 23 õhtul 4 liikmelist metsavennagruppi, keda asuti jälitama. Tekkinud tulevahetuses MVD sisevägede 138. laskurpolgu võitlejatega tapeti 2 metsavenda, teistel õnnestus varjuda. Varitsejatest tapeti punaarmeelane Korjakin ja haavati jefr. Rahmatulinit ja punaarmeelast Kolinintsevit. Tapetud metsavendi ei tuvastatud.'),
(96,'karl_pommer','Karl','Pommer',NULL,'sangaste','7.10.1927','5.02.2010','10.08.1946','15.06.1947','teenimine Saksa sõjaväes','arreteeritud','20a','Pärit Linnamäe vallast Vahemetsa talust. Mobiliseeritud Saksa sõjaväkke. Tegutses koos Johannes Heeskaga. Arreteeriti Valgas. Mõisteti 25 a, asus Uhtlagis ja Intlagis Komi ANSV-s kuni 1954, sealt Dubravlagis Mordva ANSV-s 1962 a kuni vabanemiseni.'),
(97,'hann_helbre','Hann','Helbre',NULL,'sangaste','12.06.1916','8.01.1947','?','8.01.1947','?','tapetud','30a','Hdn Tarzan. Kooraste metsavend, tegutses mitmetes ettevõtmistes koos Johannes Heeskaga. Langes haarangus.'),
(98,'alfred_hallap','Alfred','Hallap',NULL,'sangaste','29.03.1923','21.08.1946','?','21.08.1946','teeninud Saksa sõjaväes','tapetud','23a','Hdn Pakan. Kooraste metsavend, Tikerpuu salga liige. Olnud saksa sõjaväes, metsas tegutses koos Johannes Heeskaga. Langes haarangus Urvastes Hansi talu? juures.'),
(99,'asta_hallap','Asta','Hallap',NULL,'sangaste','17.07.1927','?','?','21.08.1946','?','arreteeritud','19a','Hdn Miki. Võttis koos vendadega osa mitmetest ühistest ettevõtmisetest Johannes Heeskaga. Määrati 25+5, asus Irkutski oblastis Taišetlagis, Ozjorlagis, Mordva ANSV-s Dubravlagis. Vabanes 1963 a.'),
(100,'aksel_org','Aksel','Org',NULL,'sangaste','16.03.1925','8.11.1987','?','21.11.1947','?','arreteeritud','22a','Tegutses koos Johannes Heeskaga.  Vanemad küüditati Linnamäe vallast Kurenurme külast Alaveski talust. Arreteerimise järel määrati 25 a. Asus Magadani obl. Sevvostlagis, vabanes 1956 a.'),
(101,'voldemar_org','Voldemar','Org',NULL,'sangaste','23.12.1927','?','1949','?','saadetud Julgeoleku poolt libametsavennaks','?','21a','Oli küüditatavate nimekirjas, vanemad küüditati Linnamäe vallast Kurenurme külast Alaveski talust. 1949 plaanis Julgeolek tema kinnivõtmist Erastveres, kus ta töötas. Vastavalt Julgeoleku plaanile suunati ta libametsavennana otsima Johannes Heeskat, Ants Aunverit jt.'),
(102,'viktor_lukk','Viktor','Lukk',NULL,'sangaste','25.10.1925','28.12.1945','1944','28.12.1945','täitis Haukka luurerühma ülesannet','tapetud','20a','Hdn Kusta. Teenis 1942 Eesti Leegionis. Puhkusele pääsedes ei soovinud rindele naasta. 1943 lahkus August Püvi vahendusel paadiga Soome, kus tegutses Soome peastaabi luureosakonnas ja erikoolitusel Staffai saarel. 1944. a saadeti Haukka rühma koosseisus tagasi Eestisse. Osales koos Ants Aunveri, Johannes Heeska jt-ga August Tiituse karistamisel surmaga. Langes haarangus Lükkä (Keretü) soos asuvale punkrile Nursipalu lähedal Võrumaal 28.12.1945.a.'),
(103,'eduard_savi','Eduard','Savi',NULL,'sangaste','20.12.1909','30.12.1965','?','10.12.1949','teeninud Saksa sõjaväes','arreteeritud','40a','Pärit Tõlliste vallast, Teenis Saksa sõjaväes 20. Eesti Diviisis. Võttis osa mitmest üritusest koos Johannes Heeskaga.  Arreteeriti Viljandis 10.02.1949, määrati 25+5, viibis Arhangelski oblastis Kargopollagis, Komi ANSV Minerallagis, vabanes 1955a.'),
(104,'voldemar_laats','Voldemar','Laats',NULL,'sangaste','4.05.1924','?','?','14.01.1949','?','legaliseerunud?','25a','Teenis 1944 Saksa sõjaväes piirikaitsepolgus, deserteerus. Mobiliseeriti nõukogude armeesse tööpataljoni, kus oli kuni 1947. Metsaelu ajal tegutses koos Johannes Heeskaga. Arreteeriti Urvaste vallas 14.10.1949, määrati 25 a. Viibis Komi ANSV-s, vabanes 1960. a.');
